sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	isDatePast: function(strDate) {
		//Create GlideDateTime objects for the curent date and the passed-in date
		var rightNow = new GlideDateTime();
		var testDate = new GlideDateTime(strDate);

		//If testDate is before rightNow, return true; else, return false
		if (testDate.before(rightNow)) {
			return true;
		} else {
			return false;
		}
	},
	isDateToday: function(strDate) {
		//Create GlideDateTime objects for the current date and the passed in date
		var rightNow = new GlideDateTime();
		var testDate = new GlideDateTime(strDate);

		//Get date portion of rightNow and testDate
		var today = rightNow.getLocalDate();
		var isToday = testDate.getLocalDate();

		//Compare today and isToday
		if (today.compareTo(isToday) == 0) {
			return true;
		} else {
			return false;
		}
	},
    type: 'NeedItUtils'
};
